// Declaraciones globales para el proyecto
declare global {
  interface Window {
    // Variables globales si las necesitamos
    debugUser?: any;
    emergencyAuth?: any;
  }
}

// Módulos que no tienen tipos
declare module '*.css' {
  const content: { [className: string]: string };
  export default content;
}

declare module '*.module.css' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.svg' {
  import React = require('react');
  export const ReactComponent: React.FC<React.SVGProps<SVGSVGElement>>;
  const src: string;
  export default src;
}

declare module '*.png' {
  const src: string;
  export default src;
}

declare module '*.jpg' {
  const src: string;
  export default src;
}

declare module '*.jpeg' {
  const src: string;
  export default src;
}

declare module '*.gif' {
  const src: string;
  export default src;
}

// Extensiones de tipos para casos específicos del proyecto
export {};